custom_css:
@import url('https://fonts.googleapis.com/css2?family=Kumbh+Sans:wght@400;700&display=swap');

:root {
  --brand-color: #2f4858;
}
body, body.remark-container h1, body.remark-container h2, body.remark-container h3 {
  font-family: 'Kumbh Sans', "Atkinson Hyperlegible",-apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif !important;
}
body.remark-container .remark-slide-number {
  color: white;
}

body.remark-container .my-footer {
    background: #2f4858;
    color: white;
}
body.remark-container .my-footer div {
    padding-top: 0.5em;
}
body.remark-container .my-footer a {
    color: white;
}
